import Vue from 'vue';
import VueRouter from 'vue-router';

// 主组件
import Login from '../views/Login.vue';
import Register from '../views/Register.vue';
import MainLayout from '../views/MainLayout.vue';

// MainLayout 的子组件 (视图)
import Dashboard from '../views/Dashboard.vue';
import DeviceManagement from '../views/DeviceManagement.vue';
import EnergyDataEntry from '../views/EnergyDataEntry.vue';
import EnergyDataView from '../views/EnergyDataView.vue';

Vue.use(VueRouter);

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/register',
    name: 'Register',
    component: Register
  },
  {
    path: '/app',
    component: MainLayout,
    meta: { requiresAuth: true }, //  Note: meta for parent, children can override or inherit
    children: [
      {
        path: '', 
        redirect: 'dashboard'
      },
      {
        path: 'dashboard',
        name: 'Dashboard',
        component: Dashboard,
        // meta: { requiresAuth: true } // Typically inherited from parent or explicitly set
      },
      {
        path: 'devices',
        name: 'DeviceManagement',
        component: DeviceManagement,
        // meta: { requiresAuth: true }
      },
      {
        path: 'energy-data-entry',
        name: 'EnergyDataEntry',
        component: EnergyDataEntry,
        // meta: { requiresAuth: true }
      },
      {
        path: 'energy-data',
        name: 'EnergyDataView', 
        component: EnergyDataView,
        // meta: { requiresAuth: true }
      }
    ]
  },
  {
    path: '*', 
    redirect: '/login'
  }
];

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
});

// Revised router guard
router.beforeEach((to, from, next) => {
  console.log('[Router Guard] Navigating TO:', to.fullPath, '(name:', to.name, ') FROM:', from.fullPath, '(name:', from.name, ')');
  const isAuthenticated = localStorage.getItem('user-token');
  console.log('[Router Guard] isAuthenticated (based on localStorage user-token):', isAuthenticated ? true : false, '(Token:', isAuthenticated, ')');
  const requiresAuth = to.matched.some(record => record.meta.requiresAuth);
  console.log('[Router Guard] Route requiresAuth:', requiresAuth);

  if (to.name === 'Login' || to.name === 'Register') {
    if (isAuthenticated) {
      const intendedRedirect = to.query.redirect || '/app/dashboard';
      console.log('[Router Guard] User is authenticated, on Login/Register. Redirecting to:', intendedRedirect);
      next({ path: intendedRedirect, replace: true });
    } else {
      console.log('[Router Guard] User is NOT authenticated, on Login/Register. Allowing access.');
      next();
    }
  } else if (requiresAuth) {
    if (isAuthenticated) {
      console.log('[Router Guard] Route requires auth, user IS authenticated. Allowing access.');
      next();
    } else {
      console.log('[Router Guard] Route requires auth, user is NOT authenticated. Redirecting to Login. Original intended path:', to.fullPath);
      next({ name: 'Login', query: { redirect: to.fullPath }, replace: true });
    }
  } else {
    console.log('[Router Guard] Route does not require auth. Allowing access.');
    next();
  }
});

export default router;
