import Vue from 'vue';
import VueRouter from 'vue-router';

// 主组件
import Login from '../views/Login.vue';
import Register from '../views/Register.vue';
import MainLayout from '../views/MainLayout.vue';

// MainLayout 的子组件 (视图)
import Dashboard from '../views/Dashboard.vue';
import DeviceManagement from '../views/DeviceManagement.vue';
import EnergyDataEntry from '../views/EnergyDataEntry.vue';
import EnergyDataView from '../views/EnergyDataView.vue';

Vue.use(VueRouter);

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/register',
    name: 'Register',
    component: Register
  },
  {
    path: '/app',
    component: MainLayout,
    meta: { requiresAuth: true }, //  Note: meta for parent, children can override or inherit
    children: [
      {
        path: '', 
        redirect: 'dashboard'
      },
      {
        path: 'dashboard',
        name: 'Dashboard',
        component: Dashboard,
        // meta: { requiresAuth: true } // Typically inherited from parent or explicitly set
      },
      {
        path: 'devices',
        name: 'DeviceManagement',
        component: DeviceManagement,
        // meta: { requiresAuth: true }
      },
      {
        path: 'energy-data-entry',
        name: 'EnergyDataEntry',
        component: EnergyDataEntry,
        // meta: { requiresAuth: true }
      },
      {
        path: 'energy-data',
        name: 'EnergyDataView', 
        component: EnergyDataView,
        // meta: { requiresAuth: true }
      }
    ]
  },
  {
    path: '*', 
    redirect: '/login'
  }
];

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
});

// Revised router guard
router.beforeEach((to, from, next) => {
  const isAuthenticated = localStorage.getItem('user-token');
  const requiresAuth = to.matched.some(record => record.meta.requiresAuth);

  if (to.name === 'Login' || to.name === 'Register') {
    if (isAuthenticated) {
      // User is authenticated, should not access Login/Register.
      // Redirect to the page they were trying to access before being sent to login (if any),
      // or to the dashboard.
      // Check `to.query.redirect` first (if they landed on /login?redirect=foo directly)
      // then `from.query.redirect` (if they were on /login and from had a redirect)
      const redirectPath = to.query.redirect || from.query.redirect || '/app/dashboard';
      next({ path: redirectPath });
    } else {
      // User is not authenticated, allow access to Login/Register.
      next();
    }
  } else if (requiresAuth) {
    // Route requires authentication.
    if (isAuthenticated) {
      // User is authenticated, allow access.
      next();
    } else {
      // User is not authenticated, redirect to Login, saving the intended path.
      next({ name: 'Login', query: { redirect: to.fullPath } });
    }
  } else {
    // Route does not require authentication, allow access.
    next();
  }
});

export default router;
