import Vue from 'vue'
import Vuex from 'vuex'
import axios from 'axios';

Vue.use(Vuex)

const authModule = {
  namespaced: true,
  state: {
    user: null, // 用户信息对象
    token: localStorage.getItem('user-token') || '' // Restore token state
  },
  mutations: {
    SET_TOKEN(state, token) { // Restore SET_TOKEN
      state.token = token;
      localStorage.setItem('user-token', token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    },
    CLEAR_TOKEN(state) { // Restore CLEAR_TOKEN
      state.token = '';
      localStorage.removeItem('user-token');
      delete axios.defaults.headers.common['Authorization'];
    },
    SET_USER(state, user) {
      state.user = user; 
      if (user) {
        localStorage.setItem('user-info', JSON.stringify(user)); 
      } else {
        localStorage.removeItem('user-info'); 
      }
    },
    // CLEAR_USER is effectively SET_USER(state, null), can be removed or kept if preferred
    // CLEAR_USER(state) { 
    //   state.user = null;
    //   localStorage.removeItem('user-info');
    // }
  },
  actions: {
    async login({ commit }, credentials) {
      try {
        const response = await axios.post('/auth/signin', credentials);
        const responseData = response.data;
        console.log('[Vuex Store - login action] Received responseData from /auth/signin:', JSON.stringify(responseData, null, 2)); // Log the full response data

        if (responseData && responseData.token && responseData.username) { 
          const user = {
            id: responseData.id,
            username: responseData.username,
            email: responseData.email,
            roles: responseData.roles
          };
          commit('SET_TOKEN', responseData.token); // Set the token
          commit('SET_USER', user); // Set user info
          return Promise.resolve(user);
        } else {
          throw new Error(responseData.message || 'Login failed: Invalid response from server.');
        }
      } catch (error) {
        commit('CLEAR_TOKEN'); // Clear token on login failure
        commit('SET_USER', null); 
        const errorMessage = error.response && error.response.data && error.response.data.message 
                           ? error.response.data.message 
                           : (error.message || 'Login failed due to an unexpected error.');
        return Promise.reject(new Error(errorMessage));
      }
    },
    async register(_context, userData) {
      try {
        const response = await axios.post('/auth/signup', userData);
        return Promise.resolve(response.data); 
      } catch (error) {
        const errorMessage = error.response && error.response.data && error.response.data.message
                           ? error.response.data.message
                           : (error.message || 'Registration failed due to an unexpected error.');
        return Promise.reject(new Error(errorMessage));
      }
    },
    logout({ commit }) {
      commit('CLEAR_TOKEN'); // Clear token from state, localStorage, and axios headers
      commit('SET_USER', null); 
      return Promise.resolve();
    },
    fetchUser({ commit }) { 
      const storedUser = localStorage.getItem('user-info');
      const token = localStorage.getItem('user-token'); // Also check for token
      if (token) { // If token exists, set it again (e.g. on page refresh)
        commit('SET_TOKEN', token);
      }
      if (storedUser) {
        try {
          const user = JSON.parse(storedUser);
          commit('SET_USER', user); 
        } catch (e) {
          localStorage.removeItem('user-info'); 
          commit('CLEAR_TOKEN'); // If user info is corrupt, clear token too
        }
      } else if (!token) { // If no user info and no token, ensure everything is cleared
        commit('CLEAR_TOKEN');
        commit('SET_USER', null);
      }
    }
  },
  getters: {
    // isAuthenticated: state => !!state.user, // Now based on token presence
    isAuthenticated: state => !!state.token, // Authenticated if token exists
    currentUser: state => state.user,
    userRole: state => (state.user && state.user.roles && state.user.roles.length > 0) ? state.user.roles[0] : null
  }
}

export default new Vuex.Store({
  modules: {
    auth: authModule
  }
  // 你可以在这里添加更多的模块
})
