import Vue from 'vue'
import VueRouter from 'vue-router'
// 懒加载组件，提高初始加载速度
const Login = () => import(/* webpackChunkName: "login" */ '../views/Login.vue')
const Register = () => import(/* webpackChunkName: "register" */ '../views/Register.vue')
const Dashboard = () => import(/* webpackChunkName: "dashboard" */ '../views/Dashboard.vue')
const MainLayout = () => import(/* webpackChunkName: "layout" */ '../views/MainLayout.vue')
const EnergyDataManagement = () => import(/* webpackChunkName: "energy" */ '../views/EnergyDataManagement.vue');
const DeviceManagement = () => import(/* webpackChunkName: "device" */ '../views/DeviceManagement.vue');

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/login' // 默认重定向到登录页
  },
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/register',
    name: 'Register',
    component: Register
  },
  {
    path: '/app',
    component: MainLayout, // 使用主布局组件
    children: [
      {
        path: 'dashboard',
        name: 'Dashboard',
        component: Dashboard,
        meta: { requiresAuth: true } // 示例：标记需要登录的路由
      },
      {
        path: 'energy-data',
        name: 'EnergyDataManagement',
        component: EnergyDataManagement,
        meta: { requiresAuth: true }
      },
      {
        path: 'devices',
        name: 'DeviceManagement',
        component: DeviceManagement,
        meta: { requiresAuth: true }
      }
      // 可以在这里继续添加其他子路由
    ]
  }
  // 你可以在这里添加更多的路由
]

const router = new VueRouter({
  mode: 'history', // 使用 history 模式，需要后端配合处理URL刷新
  base: process.env.BASE_URL,
  routes
})

// 简单的路由守卫示例 (可选)
router.beforeEach((to, from, next) => {
  const isAuthenticated = !!localStorage.getItem('user-token'); // 检查本地存储中是否有token

  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (!isAuthenticated) {
      next({
        path: '/login',
        query: { redirect: to.fullPath } // 保存用户尝试访问的地址，登录后可以重定向回去
      });
    } else {
      next();
    }
  } else {
    next(); // 确保一定要调用 next()
  }
});

export default router
